import axios from 'axios';

const API_URL = 'http://localhost:5000/api';

export const api = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json'
  }
});

// Vocabulary Service
export const vocabularyService = {
  // Get all vocabulary words
  getAll: () => api.get('/vocabulary'),

  // Get a specific vocabulary word by ID
  getById: (id) => api.get(`/vocabulary/${id}`),

  // Create a new vocabulary word
  create: (word) => api.post('/vocabulary', word),

  // Update a vocabulary word
  update: (id, word) => api.put(`/vocabulary/${id}`, word),

  // Delete a vocabulary word
  delete: (id) => api.delete(`/vocabulary/${id}`)
};

// User Progress Service
export const progressService = {
  // Get progress for a specific user
  getProgress: (userId) => api.get(`/progress/user/${userId}`),

  // Update progress for a vocabulary word
  updateProgress: (progressData) => api.post('/progress/update', progressData),

  // Get words due for review
  getReviewWords: (userId) => api.get(`/progress/review/${userId}`)
};
