  computed: {
    isAllSelected() {
      return this.selectedRows.length === this.rows.length;
    },
    rowIds() {
      return this.rows.map(row => row.id);
    }
  },
  methods: {
    shouldShowRow(rowId) {
      // 如果没有选中任何行，则显示所有行
      // 如果选中了某些行，则只显示被选中的行
      return this.selectedRows.length === 0 || this.selectedRows.includes(rowId);
    },
    getKana(row, column) {
      const kanaSet = this.kanaType === 'hiragana' ? this.hiragana : this.katakana;
      return kanaSet.find(kana => kana.row === row && kana.column === column) || {
        character: '',
        romaji: ''
      };
    },
    getNSpecial() {
      const kanaSet = this.kanaType === 'hiragana' ? this.hiragana : this.katakana;
      return kanaSet.find(kana => kana.row === 'x') || {
        character: '',
        romaji: ''
      };
    },
    flipCard(character) {
      if (!character) return;

      // Play the corresponding audio
      this.playKanaAudio(this.getKanaRomaji(character));
      this.flippedCards[character] = !this.flippedCards[character];

      // 自动翻回卡片
      setTimeout(() => {
        this.flippedCards[character] = false;
      }, 3000);
    },
    getKanaRomaji(character) {
      const kanaSet = this.kanaType === 'hiragana' ? this.hiragana : this.katakana;
      const kana = kanaSet.find(k => k.character === character);
      return kana ? kana.romaji : '';
    },
    playKanaAudio(romaji) {
      if (!romaji) return;
      console.log('Attempting to play audio for:', romaji);

      // Cleanup previous audio if it exists
      if (this.audio) {
        this.audio.pause();
        this.audio = null;
      }

      // 处理特殊情况，如 "chi" 应该对应 "chi.mp3"
      const audioPath = `/audio/kana/${romaji}.mp3`;
      console.log('Audio path:', audioPath);

      // Create new audio element
      this.audio = new Audio(audioPath);

      // 添加事件监听器来检测加载和错误
      this.audio.addEventListener('canplaythrough', () => {
        console.log('Audio loaded and can play');
        this.audioStatus = `音频已加载: ${romaji}`;
        this.audioSuccess = true;
      });
      this.audio.addEventListener('error', e => {
        console.error('Audio error:', e);
        this.audioStatus = `音频加载失败: ${romaji}`;
        this.audioSuccess = false;
      });

      // 播放音频
      this.audio.play().then(() => console.log('Audio playback started successfully')).catch(error => {
        console.error('Error playing audio:', error);
        this.audioStatus = `播放失败: ${error.message}`;
        this.audioSuccess = false;
      });
    },
    testAudio() {
      this.audioStatus = "正在测试音频...";

      // 尝试播放一个简单的音频
      const testAudio = new Audio('/audio/kana/a.mp3');
      testAudio.oncanplaythrough = () => {
        this.audioStatus = "音频加载成功!";
        this.audioSuccess = true;
        testAudio.play().then(() => {
          this.audioStatus = "音频播放成功!";
        }).catch(e => {
          this.audioStatus = `播放错误: ${e.message}`;
          this.audioSuccess = false;
        });
      };
      testAudio.onerror = () => {
        this.audioStatus = `音频加载错误`;
        this.audioSuccess = false;
      };
    },
    setKanaType(type) {
      this.kanaType = type;
      // 清空flippedCards对象
      Object.keys(this.flippedCards).forEach(key => {
        delete this.flippedCards[key];
      });
    },
    toggleDirection() {
      this.showRomajiFirst = !this.showRomajiFirst;
      // 清空flippedCards对象
      Object.keys(this.flippedCards).forEach(key => {
        delete this.flippedCards[key];
      });
    },
    selectSingleRow(rowId) {
      if (this.selectedRows.includes(rowId)) {
        // If the row is already selected, unselect all rows
        this.selectedRows = [];
      } else {
        // Select only this row (clearing any previously selected rows)
        this.selectedRows = [rowId];
      }
    },
    selectAllRows() {
      // If there are any rows selected, clear the selection
      // This will show all rows
      this.selectedRows = [];

      // 清空flippedCards对象
      Object.keys(this.flippedCards).forEach(key => {
        delete this.flippedCards[key];
      });
    }
